export interface AccordionItem {
    title: string;
    content: string;
    isOpen: boolean;
}

import {ECalendarValue} from '../../types/calendar-value-enum';
import {SingleCalendarValue} from '../../types/single-calendar-value';
import {ElementRef, Injectable} from '@angular/core';

import {Dayjs, UnitType} from 'dayjs';
import {CalendarValue} from '../../types/calendar-value';
import {IDate} from '../../models/date.model';
import {CalendarMode} from '../../types/calendar-mode';
import {DateValidator} from '../../types/validator.type';
import {ICalendarInternal} from '../../models/calendar.model';
import {dayjsRef} from '../../dayjs/dayjs.ref';

export interface DateLimits {
  minDate?: SingleCalendarValue;
  maxDate?: SingleCalendarValue;
  minTime?: SingleCalendarValue;
  maxTime?: SingleCalendarValue;
}

@Injectable({
  providedIn: 'root'
})
export class UtilsService {
  static debounce(func: Function, wait: number) {
    let timeout: ReturnType<typeof setTimeout>;
    return (...args: any[]) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, wait);
    };
  }

  createArray(size: number): number[] {
    return new Array(size).fill(1);
  }

  convertToDayjs(date: SingleCalendarValue, format: string): Dayjs {
    if (!date) {
      return dayjsRef();
    } else if (typeof date === 'string') {
      return dayjsRef(date, format);
    } else {
      return dayjsRef(date.toDate());
    }
  }

  isDateValid(date: string, format: string): boolean {
    if (date === '') {
      return true;
    }

    return dayjsRef(date, format, true).isValid();
  }

  // todo:: add unit test
  getDefaultDisplayDate(current: Dayjs,
                        selected: Dayjs[],
                        allowMultiSelect: boolean,
                        minDate: Dayjs): Dayjs {
    if (current) {
      return dayjsRef(current.toDate());
    } else if (minDate && minDate.isAfter(dayjsRef())) {
      return dayjsRef(minDate.toDate());
    } else if (allowMultiSelect) {
      if (selected && selected[selected.length]) {
        return dayjsRef(selected[selected.length].toDate());
      }
    } else if (selected && selected[0]) {
      return dayjsRef(selected[0].toDate());
    }

    return dayjsRef();
  }

  // todo:: add unit test
  getInputType(value: CalendarValue, allowMultiSelect: boolean): ECalendarValue {
    if (Array.isArray(value)) {
      if (!value.length) {
        return ECalendarValue.DayjsArr;
      } else if (typeof value[0] === 'string') {
        return ECalendarValue.StringArr;
      } else if (dayjsRef.isDayjs(value[0])) {
        return ECalendarValue.DayjsArr;
      }
    } else {
      if (typeof value === 'string') {
        return ECalendarValue.String;
      } else if (dayjsRef.isDayjs(value)) {
        return ECalendarValue.Dayjs;
      }
    }

    return allowMultiSelect ? ECalendarValue.DayjsArr : ECalendarValue.Dayjs;
  }

  // todo:: add unit test
  convertToDayjsArray(value: CalendarValue,
                      config: { allowMultiSelect?: boolean, format?: string }): Dayjs[] {
    let retVal: Dayjs[];
    switch (this.getInputType(value, config.allowMultiSelect ?? false)) {
      case (ECalendarValue.String):
        retVal = value ? [dayjsRef(<string>value, config.format, true)] : [];
        break;
      case (ECalendarValue.StringArr):
        retVal = (<string[]>value)
          .map(v => v ? dayjsRef(v, config.format, true) : null)
          .filter((d): d is Dayjs => d !== null && d !== undefined);
        break;
      case (ECalendarValue.Dayjs):
        retVal = value ? [dayjsRef((<Dayjs>value).toDate())] : [];
        break;
      case (ECalendarValue.DayjsArr):
        retVal = (<Dayjs[]>value || []).map(v => dayjsRef(v.toDate()));
        break;
      default:
        retVal = [];
    }

    return retVal;
  }

  // todo:: add unit test
  convertFromDayjsArray(format: string,
                        value: Dayjs[],
                        convertTo: ECalendarValue): CalendarValue {
    switch (convertTo) {
      case (ECalendarValue.String):
        return value[0] && value[0].format(format);
      case (ECalendarValue.StringArr):
        return value.filter(Boolean).map(v => v.format(format));
      case (ECalendarValue.Dayjs):
        return value[0] ? dayjsRef(value[0].toDate()) : value[0];
      case (ECalendarValue.DayjsArr):
        return value ? value.map(v => dayjsRef(v.toDate())) : value;
      default:
        return value;
    }
  }

  convertToString(value: CalendarValue, format: string): string {
    let tmpVal: string[];

    if (typeof value === 'string') {
      tmpVal = [value];
    } else if (Array.isArray(value)) {
      if (value.length) {
        tmpVal = (<SingleCalendarValue[]>value).map((v) => {
          return this.convertToDayjs(v, format).format(format);
        });
      } else {
        tmpVal = <string[]>value;
      }
    } else if (dayjsRef.isDayjs(value)) {
      tmpVal = [value.format(format)];
    } else {
      return '';
    }

    return tmpVal.filter(Boolean).join(' | ');
  }

  // todo:: add unit test
  clearUndefined<T extends { [key: string]: any }>(obj: T): T {
    if (!obj) {
      return obj;
    }

    Object.keys(obj).forEach((key) => (obj[key] === undefined) && delete obj[key]);
    return obj;
  }

  updateSelected(isMultiple: boolean,
                 currentlySelected: Dayjs[],
                 date: IDate,
                 granularity: UnitType = 'day'): Dayjs[] {
    if (isMultiple) {
      return !date.selected
        ? currentlySelected.concat([date.date])
        : currentlySelected.filter(d => !d.isSame(date.date, granularity));
    } else {
      return !date.selected ? [date.date] : [];
    }
  }

  closestParent(element: HTMLElement, selector: string): HTMLElement | null {
    if (!element) {
      return null;
    }
    const match = <HTMLElement>element.querySelector(selector);
    if (match) {
      return match;
    }
    if (element.parentElement) {
      return this.closestParent(element.parentElement, selector);
    }
    return null;
  }

  onlyTime(m: Dayjs): Dayjs {
    return m && dayjsRef.isDayjs(m) ? dayjsRef(m.format('HH:mm:ss'), 'HH:mm:ss') : dayjsRef('00:00:00', 'HH:mm:ss');
  }

  granularityFromType(calendarType: CalendarMode): UnitType {
    switch (calendarType) {
      case 'time':
        return 'second';
      case 'daytime':
        return 'second';
      default:
        return calendarType;
    }
  }

  createValidator({minDate, maxDate, minTime, maxTime}: DateLimits,
                  format: string,
                  calendarType: CalendarMode): DateValidator {
    let isValid: boolean;
    let value: Dayjs[];
    const validators: { key: string; isValid: () => boolean }[] = [];
    const granularity = this.granularityFromType(calendarType);

    if (minDate) {
      const md = this.convertToDayjs(minDate, format);
      validators.push({
        key: 'minDate',
        isValid: () => {
          const _isValid = value.every(val => val.isSameOrAfter(md, granularity));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    if (maxDate) {
      const md = this.convertToDayjs(maxDate, format);
      validators.push({
        key: 'maxDate',
        isValid: () => {
          const _isValid = value.every(val => val.isSameOrBefore(md, granularity));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    if (minTime) {
      const md = this.onlyTime(this.convertToDayjs(minTime, format));
      validators.push({
        key: 'minTime',
        isValid: () => {
          const _isValid = value.every(val => this.onlyTime(val).isSameOrAfter(md));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    if (maxTime) {
      const md = this.onlyTime(this.convertToDayjs(maxTime, format));
      validators.push({
        key: 'maxTime',
        isValid: () => {
          const _isValid = value.every(val => this.onlyTime(val).isSameOrBefore(md));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    return (inputVal: CalendarValue) => {
      isValid = true;

      value = this.convertToDayjsArray(inputVal, {
        format,
        allowMultiSelect: true
      }).filter(Boolean);

      if (!value.every(val => val.isValid())) {
        return {
          format: {
            given: inputVal
          }
        };
      }

      interface Validator {
        key: string;
        isValid: () => boolean;
      }

      interface ErrorMap {
        [key: string]: {
          given: Dayjs[];
        };
      }

      interface Validator {
        key: string;
        isValid: () => boolean;
      }

      const errors: ErrorMap = validators.reduce<ErrorMap>((map: ErrorMap, err: Validator) => {
        if (!err.isValid()) {
          map[err.key] = {
        given: value
          };
        }

        return map;
      }, {});

      return !isValid ? errors : {};
    };
  }

  datesStringToStringArray(value: string): string[] {
    return (value || '').split('|').map(m => m.trim()).filter(Boolean);
  }

  getValidDayjsArray(value: string, format: string): Dayjs[] {
    return this.datesStringToStringArray(value)
      .filter(d => this.isDateValid(d, format))
      .map(d => dayjsRef(d, format));
  }

  shouldShowCurrent(showGoToCurrent: boolean,
                    mode: CalendarMode,
                    min: Dayjs,
                    max: Dayjs): boolean {
    return showGoToCurrent &&
      mode !== 'time' &&
      this.isDateInRange(dayjsRef(), min, max);
  }

  isDateInRange(date: Dayjs, from: Dayjs, to: Dayjs): boolean {
    if (!date) {
      return false;
    }

    if (!from && !to) {
      return true;
    }

    if (!from && to) {
      return date.isSameOrBefore(to);
    }

    if (from && !to) {
      return date.isSameOrAfter(from);
    }

    return date.isBetween(from, to, 'day', '[]');
  }

  convertPropsToDayjs(obj: { [key: string]: any }, format: string, props: string[]): void {
    props.forEach((prop) => {
      if (obj.hasOwnProperty(prop)) {
        obj[prop] = this.convertToDayjs(obj[prop], format);
      }
    });
  }

  shouldResetCurrentView<T extends ICalendarInternal>(prevConf: T, currentConf: T): boolean {
    if (prevConf && currentConf) {
      if (!prevConf.min && currentConf.min) {
        return true;
      } else if (prevConf.min && currentConf.min && !prevConf.min.isSame(currentConf.min, 'd')) {
        return true;
      } else if (!prevConf.max && currentConf.max) {
        return true;
      } else if (prevConf.max && currentConf.max && !prevConf.max.isSame(currentConf.max, 'd')) {
        return true;
      }

      return false;
    }

    return false;
  }

  getNativeElement(elem: HTMLElement | string | ElementRef): HTMLElement | null {
    if (!elem) {
      return null;
    } else if (typeof elem === 'string') {
      return document.querySelector(elem);
    } else if (elem instanceof ElementRef) {
      return elem.nativeElement;
    } else {
      return elem;
    }
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule, DatePipe } from '@angular/common';
import { FormsModule } from '@angular/forms';

export interface DateOptions {
  dateFormat?: string;
  selectedEndDate?: Date;
  selectedStartDate?: Date;
  disabledWeekends?: boolean;
  disableRangePciker?: boolean;
  isStepperYearShown?: boolean;
}

@Component({
  selector: 'cs-date-picker',
  standalone: true,
  templateUrl: './cs-date-picker.component.html',
  styleUrl: './cs-date-picker.component.scss',
  imports: [CommonModule, FormsModule],
  providers: [DatePipe],
})
export class CsDatePickerComponent {
  @Output() dateObject = new EventEmitter<Object>();
  isRightChervronEnable = false;

  @Input()
  set dateOptions(dateOptions: DateOptions) {
    if (dateOptions) {
      this.setDateOptions(dateOptions);
    }
  }

  disabledWeekends = true;
  disableRangePciker = false;
  @Input() isStepperYearShown = true;
  dateFormat = '';

  showDropdownWithEmptyContent = false;
  days: (Date | undefined)[] = [];
  years: number[] = [];
  months: Date[] = [];
  @Input() offsetYearsMenu = 5;
  @Input() isSelectionDayAfterCurrentDayEnable = true;
  isDayselected = false;
  isDatePickerClicked = false;
  radioButtonDate = new Date();
  dateTosubmit = '';
  tabBtnActive: number = -1;

  currentDate: Date = new Date();
  endDate: Date = new Date();

  selectedStartDate: Date | undefined;
  selectedStartDateTMP: Date | undefined;
  selectedEndDate: Date | undefined;

  dateDisplayed: string = '';
  today = new Date().toLocaleDateString();
  currentDay = new Date();
  toggleYearChoice = false;
  toggleMonthChoice = false;
  yearchose = 0;
  toggleDate = new Date();
  abbreviatedMonth: string[] = [];
  ngOnInit(): void {
    this.renderCalendar();
    this.setYearsMenuToChoose();
    this.getMonthAbreviated();
  }

  constructor(private datePipe: DatePipe) {}

  //to populate the calendar
  renderCalendar() {
    this.days = [];
    this.currentDate.setDate(1);
    this.currentDate.setHours(0, 0, 0, 0);

    this.endDate = new Date(this.currentDate);
    this.endDate.setMonth(this.endDate.getMonth() + 1);
    this.endDate.setDate(0);

    let dayFromNextMonthToDisplay = this.endDate.getDay()
      ? 7 - this.endDate.getDay()
      : 0;

    for (
      let indexDay = this.currentDate.getDate();
      indexDay <= this.endDate.getDate() + dayFromNextMonthToDisplay;
      indexDay++
    ) {
      let dateAtIndex: Date = new Date(this.currentDate);
      dateAtIndex.setDate(indexDay);

      if (indexDay === this.currentDate.getDate()) {
        let spliceNumberElement = this.currentDate.getDay() || 7;

        for (let i = 1; i < spliceNumberElement; i++) {
          this.days.splice(0, 0, undefined);
        }
      }
      this.days.push(dateAtIndex);
    }
  }

  setYearsMenuToChoose() {
    // offset number of years before current year
    // ex: this.offsetYearsMenu = 5

    //get current year - offset
    // ex: initialYearMenu = 2017

    let initialYearMenu = this.currentDate.getFullYear() - this.offsetYearsMenu;
    console.log(this.currentDate.getFullYear());

    // for loop initialYearMenu untill current year
    // initialYearMenu++
    for (
      let index = initialYearMenu;
      index <= this.currentDate.getFullYear();
      index++
    ) {
      this.years.push(index);
    }
  }

  getMonthAbreviated() {
    let locale = 'en-GB';
    let objDate = new Date();
    for (let index = 0; index <= 11; index++) {
      this.abbreviatedMonth.push(
        new Date(objDate.setMonth(index)).toLocaleString(locale, {
          month: 'short',
        })
      );
    }
  }

  setMonthMenuToChoose() {
    this.toggleMonthChoice = true;

    let res = [];
    let yearChosen = this.toggleDate.getFullYear();
    let monthChoice =
      yearChosen === new Date().getFullYear() ? new Date().getMonth() : 11;
    const endDate = new Date(yearChosen, monthChoice, 1);
    const startDate = new Date(endDate.setFullYear(endDate.getFullYear()));
    startDate.setDate(1);
    startDate.setMonth(0);
    for (let index = 0; index <= endDate.getMonth(); index++) {
      let tempDate = new Date(startDate.setMonth(index));
      res.push(tempDate);
    }
    this.months = res;
  }

  getSelectedMonth(month: Date): void {
    this.currentDate = new Date(month);
    this.toggleDate = this.currentDate;
    this.toggleMonthChoice = false;
    this.checkIsRightChervronEnable(0);
    this.renderCalendar();
  }

  checkIsRightChervronEnable(offset: number) {
    this.currentDate = new Date(
      this.currentDate.setMonth(this.currentDate.getMonth() + offset)
    );
    let monthOffset = new Date();
    monthOffset.setMonth(monthOffset.getMonth() - 1);
    this.isRightChervronEnable = this.currentDate < monthOffset;
  }

  moveToMonthOffset(event: any, offset: number) {
    event.stopPropagation();
    this.checkIsRightChervronEnable(offset);

    this.toggleDate = this.currentDate;
    this.renderCalendar();
  }

  moveToYearOffset(event: any, offset: number) {
    event.stopPropagation();
    const target = Number((event.target as HTMLElement)?.textContent);
    console.log(target);

    let calculateOffset = this.years.length - (offset + 1);
    this.toggleDate = new Date(
      this.toggleDate.setFullYear(target, this.toggleDate.getMonth())
    );
    this.currentDate = new Date(
      this.currentDate.setFullYear(new Date().getFullYear() - calculateOffset)
    );
    this.checkIsRightChervronEnable(0);
    this.setTabBtnActive(offset);
    this.renderCalendar();
    this.toggleYearChoice = false;
    this.setMonthMenuToChoose();
  }

  getSelectedDay(day: Date | undefined, e: any): void {
    if (day === undefined) return;

    if (e.type === 'click' || e.type === 'change') {
      e.stopPropagation();
      this.setTabBtnActive(-1);

      let weekendDay = new Date(day).getDay();
      if (this.disabledWeekends && (weekendDay === 6 || weekendDay === 0))
        return;

      // disable current day + 1 selection if needed
      this.currentDay = new Date();
      if (day > this.currentDay && !this.isSelectionDayAfterCurrentDayEnable)
        return;

      if (this.disableRangePciker) {
        this.selectedStartDate = new Date(day);
        this.selectedEndDate = new Date(day);
      } else {
        if (
          this.selectedStartDate === undefined ||
          day < this.selectedStartDate ||
          (day !== this.selectedStartDate && this.isDatePickerClicked === false)
        ) {
          this.selectedStartDate = new Date(day);
          this.isDatePickerClicked = true;
          // reseting endate to make sure it look like DD/MM/YYYY
          this.selectedEndDate = undefined;
        } else if (this.selectedStartDate && this.isDatePickerClicked) {
          this.selectedEndDate = new Date(day);
          this.isDatePickerClicked = false;
        } else {
          this.selectedStartDate = new Date(day);
          this.selectedEndDate = undefined;
          this.isDatePickerClicked = true;
        }
      }

      this.updateDateDisplay();
      if (
        (this.selectedStartDate !== undefined &&
          this.selectedEndDate !== undefined) ||
        this.disableRangePciker
      )
        this.emitDateToParent();
    }

    if (
      e.type === 'mouseenter' &&
      this.isDatePickerClicked &&
      !this.disableRangePciker
    ) {
      this.selectedEndDate = new Date(day);
    }
  }

  leavePicker() {
    if (this.isDatePickerClicked && this.selectedStartDate)
      this.selectedEndDate = new Date(this.selectedStartDate);
  }

  setSelectionDate(numberOfDays: number, activeBtnIndex: number) {
    let endDate = new Date();
    endDate.setHours(0, 0, 0, 0);
    this.selectedEndDate = new Date(endDate);
    let startDate = new Date(this.selectedEndDate);
    startDate.setDate(this.selectedEndDate.getDate() - numberOfDays);
    this.selectedStartDate = new Date(startDate);
    this.currentDate = new Date();
    this.setTabBtnActive(activeBtnIndex);
    this.updateDateDisplay();
    this.renderCalendar();
    this.emitDateToParent();
  }

  setDateOptions(dateOptions: DateOptions) {
    this.disabledWeekends =
      dateOptions.disabledWeekends !== undefined
        ? dateOptions.disabledWeekends
        : true;
    this.disableRangePciker =
      dateOptions.disableRangePciker !== undefined
        ? dateOptions.disableRangePciker
        : false;
    this.isStepperYearShown =
      dateOptions.isStepperYearShown !== undefined
        ? dateOptions.isStepperYearShown
        : true;

    this.dateFormat = dateOptions.dateFormat || 'dd/MM/yyyy';

    this.selectedEndDate = dateOptions.selectedEndDate
      ? new Date(dateOptions.selectedEndDate)
      : undefined;
    this.selectedStartDate = dateOptions.selectedStartDate
      ? new Date(dateOptions.selectedStartDate)
      : undefined;
    this.selectedStartDate = dateOptions.selectedStartDate
      ? new Date(dateOptions.selectedStartDate)
      : undefined;
    this.currentDate = new Date();

    if (this.selectedEndDate) {
      this.selectedEndDate.setHours(0, 0, 0, 0);
    }

    if (this.selectedStartDate) {
      this.selectedStartDate.setHours(0, 0, 0, 0);
    }

    this.updateDateDisplay();
    this.renderCalendar();
    this.emitDateToParent();
  }

  emitDateToParent() {
    this.dateObject.emit({
      startDate: this.selectedStartDate,
      todate: this.selectedEndDate,
    });
  }

  closeDropDown(e: any) {
    e.stopPropagation();
    if (e.relatedTarget === null && e.type === 'blur') {
      this.showDropdownWithEmptyContent = false;
    }
    if (e.relatedTarget === null && e.type === 'focusout') {
      this.showDropdownWithEmptyContent = false;
    }

    if (
      this.isDatePickerClicked &&
      !this.toggleYearChoice &&
      !this.toggleMonthChoice
    ) {
      this.selectedEndDate = this.selectedStartDate;
      // this.isDatePickerClicked = false;
      // console.log('ici');
    }
  }

  openDropDown() {
    this.showDropdownWithEmptyContent = !this.showDropdownWithEmptyContent;
  }

  setTabBtnActive(index: number) {
    this.tabBtnActive = index;
  }

  isSelectedStartOrEndDate(day: Date): boolean {
    return (
      (this.selectedStartDate !== undefined &&
        this.selectedStartDate.getTime() === day.getTime()) ||
      (this.selectedEndDate !== undefined &&
        this.selectedEndDate.getTime() === day.getTime())
    );
  }

  isSelectedStartDate(day: Date): boolean {
    return (
      this.selectedStartDate !== undefined &&
      this.selectedStartDate.getTime() === day.getTime()
    );
  }

  updateDateDisplay() {
    if (this.selectedStartDate) {
      this.dateDisplayed =
        this.datePipe.transform(this.selectedStartDate, this.dateFormat) || '';
      if (!this.disableRangePciker) {
        this.dateDisplayed += this.selectedEndDate
          ? ' - ' +
            this.datePipe.transform(this.selectedEndDate, this.dateFormat)
          : ' - ' + this.dateFormat.toUpperCase();
      }
    } else {
      this.dateDisplayed = '';
    }
  }

  getDaysOfWeekInAbreviateAndLocaleFormat() {
    const weekdayDateMap: any = {
      Mon: new Date('2020-01-06T00:00:00.000Z'),
      Tue: new Date('2020-01-07T00:00:00.000Z'),
      Wed: new Date('2020-01-08T00:00:00.000Z'),
      Thu: new Date('2020-01-09T00:00:00.000Z'),
      Fri: new Date('2020-01-10T00:00:00.000Z'),
      Sat: new Date('2020-01-11T00:00:00.000Z'),
      Sun: new Date('2020-01-12T00:00:00.000Z'),
    };
    const shortWeekdays = Object.keys(weekdayDateMap);

    const getDayOfWeek = (shortName: any, locale = 'en-US', length = 'short') =>
      new Intl.DateTimeFormat(locale, { weekday: 'narrow' }).format(
        weekdayDateMap[shortName]
      );

    const getDaysOfWeek = (locale = 'en-US', length = 'short') =>
      shortWeekdays.map((shortName) => getDayOfWeek(shortName, locale, length));

    return getDaysOfWeek();
  }

  createDateObject(value: any) {
    const date = new Date(value.split('/').reverse().join('/'));
    return date.toString() == 'Invalid Date' ? null : date;
  }

  // TODO in the future
  dateDisplayedChange(event: any) {
    // add in the input
    // (change)="dateDisplayedChange($event.target.value)"
    let dateChangeArray = event.target.value.replace(/\s/g, '').split('-');
    console.log(event);

    let startDate = this.createDateObject(dateChangeArray[0]);

    let endDate = this.createDateObject(dateChangeArray[1]);

    if (startDate && endDate && startDate <= endDate) {
      this.getSelectedDay(startDate, event);
      this.getSelectedDay(endDate, event);
    }
  }
}

import { Injectable, Signal, signal } from '@angular/core';
import { Observable, forkJoin } from 'rxjs';
import { catchError, delay, map, mergeMap } from 'rxjs/operators';
import { ajax } from 'rxjs/ajax';

interface Pokemon {
  name: string;
  sprites?: {
    front_default: string;
  };
  image: string;
  id: number;
}

interface HttpResponse<T> {
  results: T;
  error: string | undefined;
  isLoading: boolean;
}

@Injectable({
  providedIn: 'root',
})
export class PokemonService {
  url = 'https://pokeapi.co/api/v2/pokemon?offset=0&limit=5';

  pokemons = signal<HttpResponse<Pokemon[]>>({
    results: [],
    error: undefined,
    isLoading: true,
  });

  pokemonDetail = signal<HttpResponse<Pokemon | null>>({
    results: null,
    error: undefined,
    isLoading: false,
  });

  fetchData<T>(url: string): Observable<T> {
    return ajax.getJSON<T>(url).pipe(delay(2000));
  }

  mapPokemons(pokemons: Pokemon[]): Pokemon[] {
    return pokemons.map((pokemon) => ({
      name: pokemon.name,
      image: pokemon.sprites?.front_default ?? '',
      id: pokemon.id,
    }));
  }

  getPokemons(): Observable<HttpResponse<Pokemon[]>> {
    return this.fetchData<HttpResponse<Pokemon[]>>(this.url).pipe(
      mergeMap((data) =>
        forkJoin(
          data.results.map((_, index) =>
            this.fetchData<Pokemon>(
              `https://pokeapi.co/api/v2/pokemon/${index + 1}/`
            )
          )
        )
      ),
      map((pokemons) => ({
        results: this.mapPokemons(pokemons),
        error: undefined,
        isLoading: false,
      })),
      catchError((error) => {
        return [{ results: [], error: '' + error.message, isLoading: false }];
      })
    );
  }

  getPokemonDetails(
    pokemon: Pokemon
  ): Observable<HttpResponse<Pokemon | null>> {
    return this.fetchData<Pokemon>(
      `https://pokeapi.co/api/v2/pokemon/${pokemon.id}/`
    ).pipe(
      map((data) => ({
        results: {
          name: data.name,
          image: data.sprites?.front_default ?? '',
          id: data.id,
        },
        error: undefined,
        isLoading: false,
      })),
      catchError((error) => {
        return [
          {
            results: null,
            error: 'Failed to fetch PokÃ©mon details',
            isLoading: false,
          },
        ];
      })
    );
  }

  setPokemonsLoadingState() {
    this.pokemons.set({
      results: this.pokemons().results,
      error: undefined,
      isLoading: true,
    });
  }

  setPokemonsSuccessState(results: Pokemon[]) {
    this.pokemons.set({
      results,
      error: undefined,
      isLoading: false,
    });
  }

  setPokemonsErrorState(error: any) {
    this.pokemons.set({
      results: this.pokemons().results,
      error: '' + error.message,
      isLoading: false,
    });
  }

  setPokemonDetailLoadingState() {
    this.pokemonDetail.set({
      results: this.pokemonDetail().results,
      error: undefined,
      isLoading: true,
    });
  }

  setPokemonDetailSuccessState(results: Pokemon) {
    this.pokemonDetail.set({
      results,
      error: undefined,
      isLoading: false,
    });
  }

  setPokemonDetailErrorState(error: any) {
    this.pokemonDetail.set({
      results: this.pokemonDetail().results,
      error: '' + error.message,
      isLoading: false,
    });
  }
}

