import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PokemonService } from './pokemon.service';

interface Pokemon {
  name: string;
  sprites?: {
    front_default: string;
  };
  image: string;
  id: number;
}

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html',
  standalone: true,
  imports: [CommonModule],
})
export class FetchDataComponent implements OnInit {
  pokemonService = inject(PokemonService);

  ngOnInit() {
    this.fetchPokemons();
  }

  fetchPokemons() {
    this.pokemonService.setPokemonsLoadingState();

    this.pokemonService.getPokemons().subscribe({
      next: (response) => {
        this.pokemonService.setPokemonsSuccessState(response.results);
      },
      error: (error) => {
        this.pokemonService.setPokemonsErrorState(error);
      },
    });
  }

  getPokemonDetails(pokemon: Pokemon) {
    this.pokemonService.setPokemonsSuccessState([]);
    this.pokemonService.setPokemonDetailLoadingState();

    this.pokemonService.getPokemonDetails(pokemon).subscribe({
      next: (response) => {
        if (response.results) {
          this.pokemonService.setPokemonDetailSuccessState(response.results);
        } else {
          this.pokemonService.setPokemonDetailErrorState(
            new Error('Pokemon details not found')
          );
        }
      },
      error: (err) => {
        this.pokemonService.setPokemonDetailErrorState(err);
      },
    });
  }

  goBackToPokemonsList() {
    this.pokemonService.pokemonDetail.set({
      results: null,
      error: undefined,
      isLoading: false,
    });
    this.fetchPokemons();
  }
}

import { Injectable, Signal, signal } from '@angular/core';
import { Observable, forkJoin } from 'rxjs';
import { catchError, delay, map, mergeMap } from 'rxjs/operators';
import { ajax } from 'rxjs/ajax';

interface Pokemon {
  name: string;
  sprites?: {
    front_default: string;
  };
  image: string;
  id: number;
}

interface HttpResponse<T> {
  results: T;
  error: string | undefined;
  isLoading: boolean;
}

@Injectable({
  providedIn: 'root',
})
export class PokemonService {
  url = 'https://pokeapi.co/api/v2/pokemon?offset=0&limit=5';

  pokemons = signal<HttpResponse<Pokemon[]>>({
    results: [],
    error: undefined,
    isLoading: true,
  });

  pokemonDetail = signal<HttpResponse<Pokemon | null>>({
    results: null,
    error: undefined,
    isLoading: false,
  });

  fetchData<T>(url: string): Observable<T> {
    return ajax.getJSON<T>(url).pipe(delay(2000));
  }

  mapPokemons(pokemons: Pokemon[]): Pokemon[] {
    return pokemons.map((pokemon) => ({
      name: pokemon.name,
      image: pokemon.sprites?.front_default ?? '',
      id: pokemon.id,
    }));
  }

  getPokemons(): Observable<HttpResponse<Pokemon[]>> {
    return this.fetchData<HttpResponse<Pokemon[]>>(this.url).pipe(
      mergeMap((data) =>
        forkJoin(
          data.results.map((_, index) =>
            this.fetchData<Pokemon>(
              `https://pokeapi.co/api/v2/pokemon/${index + 1}/`
            )
          )
        )
      ),
      map((pokemons) => ({
        results: this.mapPokemons(pokemons),
        error: undefined,
        isLoading: false,
      })),
      catchError((error) => {
        return [{ results: [], error: '' + error.message, isLoading: false }];
      })
    );
  }

  getPokemonDetails(
    pokemon: Pokemon
  ): Observable<HttpResponse<Pokemon | null>> {
    return this.fetchData<Pokemon>(
      `https://pokeapi.co/api/v2/pokemon/${pokemon.id}/`
    ).pipe(
      map((data) => ({
        results: {
          name: data.name,
          image: data.sprites?.front_default ?? '',
          id: data.id,
        },
        error: undefined,
        isLoading: false,
      })),
      catchError((error) => {
        return [
          {
            results: null,
            error: 'Failed to fetch PokÃ©mon details',
            isLoading: false,
          },
        ];
      })
    );
  }

  setPokemonsLoadingState() {
    this.pokemons.set({
      results: this.pokemons().results,
      error: undefined,
      isLoading: true,
    });
  }

  setPokemonsSuccessState(results: Pokemon[]) {
    this.pokemons.set({
      results,
      error: undefined,
      isLoading: false,
    });
  }

  setPokemonsErrorState(error: any) {
    this.pokemons.set({
      results: this.pokemons().results,
      error: '' + error.message,
      isLoading: false,
    });
  }

  setPokemonDetailLoadingState() {
    this.pokemonDetail.set({
      results: this.pokemonDetail().results,
      error: undefined,
      isLoading: true,
    });
  }

  setPokemonDetailSuccessState(results: Pokemon) {
    this.pokemonDetail.set({
      results,
      error: undefined,
      isLoading: false,
    });
  }

  setPokemonDetailErrorState(error: any) {
    this.pokemonDetail.set({
      results: this.pokemonDetail().results,
      error: '' + error.message,
      isLoading: false,
    });
  }
}

import {
  Directive,
  ElementRef,
  Renderer2,
  HostListener,
  Input,
  OnChanges,
  SimpleChanges,
} from '@angular/core';

interface StyleConfig {
  borderColor?: string;
  focusBorderColor?: string;
  padding?: string;
  borderRadius?: string;
}

@Directive({
  selector: '[appCustomInputStyle]',
})
export class CustomInputStyleDirective implements OnChanges {
  @Input('appCustomInputStyle') styleConfig: StyleConfig = {
    borderColor: '#ccc',
    focusBorderColor: '#007bff',
    padding: '10px',
    borderRadius: '4px',
  };

  constructor(private el: ElementRef, private renderer: Renderer2) {
    this.setStyle();
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes) {
      this.setStyle();
    }
  }

  private setStyle() {
    const { borderColor, padding, borderRadius } = this.styleConfig;
    this.renderer.setStyle(
      this.el.nativeElement,
      'border',
      `1px solid ${borderColor}`
    );
    this.renderer.setStyle(this.el.nativeElement, 'padding', padding);
    this.renderer.setStyle(this.el.nativeElement, 'borderRadius', borderRadius);
    this.renderer.setStyle(this.el.nativeElement, 'outline', 'none');
    this.renderer.setStyle(
      this.el.nativeElement,
      'transition',
      'border-color 0.3s'
    );
  }

  @HostListener('focus') onFocus() {
    this.renderer.setStyle(
      this.el.nativeElement,
      'borderColor',
      this.styleConfig.focusBorderColor
    );
  }

  @HostListener('blur') onBlur() {
    this.renderer.setStyle(
      this.el.nativeElement,
      'borderColor',
      this.styleConfig.borderColor
    );
  }
}

