import { Injectable, Signal, signal } from '@angular/core';
import { Observable, forkJoin } from 'rxjs';
import { catchError, delay, map, mergeMap } from 'rxjs/operators';
import { ajax } from 'rxjs/ajax';

interface Pokemon {
  name: string;
  sprites?: {
    front_default: string;
  };
  image: string;
  id: number;
}

interface HttpResponse<T> {
  results: T;
  error: string | undefined;
  isLoading: boolean;
}

@Injectable({
  providedIn: 'root',
})
export class PokemonService {
  url = 'https://pokeapi.co/api/v2/pokemon?offset=0&limit=5';

  pokemons = signal<HttpResponse<Pokemon[]>>({
    results: [],
    error: undefined,
    isLoading: true,
  });

  pokemonDetail = signal<HttpResponse<Pokemon | null>>({
    results: null,
    error: undefined,
    isLoading: false,
  });

  fetchData<T>(url: string): Observable<T> {
    return ajax.getJSON<T>(url).pipe(delay(2000));
  }

  mapPokemons(pokemons: Pokemon[]): Pokemon[] {
    return pokemons.map((pokemon) => ({
      name: pokemon.name,
      image: pokemon.sprites?.front_default ?? '',
      id: pokemon.id,
    }));
  }

  getPokemons(): Observable<HttpResponse<Pokemon[]>> {
    return this.fetchData<HttpResponse<Pokemon[]>>(this.url).pipe(
      mergeMap((data) =>
        forkJoin(
          data.results.map((_, index) =>
            this.fetchData<Pokemon>(
              `https://pokeapi.co/api/v2/pokemon/${index + 1}/`
            )
          )
        )
      ),
      map((pokemons) => ({
        results: this.mapPokemons(pokemons),
        error: undefined,
        isLoading: false,
      })),
      catchError((error) => {
        return [{ results: [], error: '' + error.message, isLoading: false }];
      })
    );
  }

  getPokemonDetails(
    pokemon: Pokemon
  ): Observable<HttpResponse<Pokemon | null>> {
    return this.fetchData<Pokemon>(
      `https://pokeapi.co/api/v2/pokemon/${pokemon.id}/`
    ).pipe(
      map((data) => ({
        results: {
          name: data.name,
          image: data.sprites?.front_default ?? '',
          id: data.id,
        },
        error: undefined,
        isLoading: false,
      })),
      catchError((error) => {
        return [
          {
            results: null,
            error: 'Failed to fetch PokÃ©mon details',
            isLoading: false,
          },
        ];
      })
    );
  }

  setPokemonsLoadingState() {
    this.pokemons.set({
      results: this.pokemons().results,
      error: undefined,
      isLoading: true,
    });
  }

  setPokemonsSuccessState(results: Pokemon[]) {
    this.pokemons.set({
      results,
      error: undefined,
      isLoading: false,
    });
  }

  setPokemonsErrorState(error: any) {
    this.pokemons.set({
      results: this.pokemons().results,
      error: '' + error.message,
      isLoading: false,
    });
  }

  setPokemonDetailLoadingState() {
    this.pokemonDetail.set({
      results: this.pokemonDetail().results,
      error: undefined,
      isLoading: true,
    });
  }

  setPokemonDetailSuccessState(results: Pokemon) {
    this.pokemonDetail.set({
      results,
      error: undefined,
      isLoading: false,
    });
  }

  setPokemonDetailErrorState(error: any) {
    this.pokemonDetail.set({
      results: this.pokemonDetail().results,
      error: '' + error.message,
      isLoading: false,
    });
  }
}

import {Injectable} from '@angular/core';

import {UtilsService} from '../common/services/utils/utils.service';
import {ITimeSelectConfig, ITimeSelectConfigInternal} from './time-select-config.model';
import {Dayjs} from 'dayjs';
import {dayjsRef} from '../common/dayjs/dayjs.ref';

export type TimeUnit = 'hour' | 'minute' | 'second';
export const FIRST_PM_HOUR = 12;

@Injectable({
  providedIn: 'root'
})
export class TimeSelectService {
  readonly DEFAULT_CONFIG: ITimeSelectConfigInternal = {
    hours12Format: 'hh',
    hours24Format: 'HH',
    meridiemFormat: 'A',
    minutesFormat: 'mm',
    minutesInterval: 1,
    secondsFormat: 'ss',
    secondsInterval: 1,
    showSeconds: false,
    showTwentyFourHours: false,
    timeSeparator: ':',
  };

  constructor(private readonly utilsService: UtilsService) {
  }

  getConfig(config: ITimeSelectConfig): ITimeSelectConfigInternal {
    const timeConfigs = {
      maxTime: config?.maxTime ? this.utilsService?.onlyTime(config.maxTime) : undefined,
      minTime: config?.minTime ? this.utilsService?.onlyTime(config.minTime) : undefined
    };

    return <ITimeSelectConfigInternal>{
      ...this.DEFAULT_CONFIG,
      ...this.utilsService?.clearUndefined(config) ?? {},
      ...timeConfigs
    };
  }

  getTimeFormat(config: ITimeSelectConfigInternal): string {
    return (config?.showTwentyFourHours ? config?.hours24Format : config?.hours12Format)
      + (config?.timeSeparator ?? ':') + (config?.minutesFormat ?? 'mm')
      + (config?.showSeconds ? ((config?.timeSeparator ?? ':') + (config?.secondsFormat ?? 'ss')) : '')
      + (config?.showTwentyFourHours ? '' : ' ' + (config?.meridiemFormat ?? 'A'));
  }

  getHours(config: ITimeSelectConfigInternal, t: Dayjs | null): string {
  const time = t || dayjsRef();
  return time?.format(config?.showTwentyFourHours ? config?.hours24Format : config?.hours12Format) ?? '';
  }

  getMinutes(config: ITimeSelectConfigInternal, t: Dayjs | null): string {
  const time = t || dayjsRef();
  return time?.format(config?.minutesFormat ?? 'mm') ?? '';
  }

  getSeconds(config: ITimeSelectConfigInternal, t: Dayjs | null): string {
  const time = t || dayjsRef();
  return time?.format(config?.secondsFormat ?? 'ss') ?? '';
  }

  getMeridiem(config: ITimeSelectConfigInternal, time: Dayjs): string {
  return time?.format(config?.meridiemFormat ?? 'A') ?? '';
  }

  decrease(config: ITimeSelectConfigInternal, time: Dayjs, unit: TimeUnit): Dayjs {
    let amount: number = 1;
    switch (unit) {
      case 'minute':
        amount = config?.minutesInterval ?? 1;
        break;
      case 'second':
        amount = config?.secondsInterval ?? 1;
        break;
    }
    return time?.subtract(amount, unit) ?? time;
  }

  increase(config: ITimeSelectConfigInternal, time: Dayjs, unit: TimeUnit): Dayjs {
    let amount: number = 1;
    switch (unit) {
      case 'minute':
        amount = config?.minutesInterval ?? 1;
        break;
      case 'second':
        amount = config?.secondsInterval ?? 1;
        break;
    }
    return time?.add(amount, unit) ?? time;
  }

  toggleMeridiem(time: Dayjs): Dayjs {
    if (!time) return time;
    if (time.hour() < FIRST_PM_HOUR) {
      return time.add(12, 'hour');
    } else {
      return time.subtract(12, 'hour');
    }
  }

  shouldShowDecrease(config: ITimeSelectConfigInternal, time: Dayjs, unit: TimeUnit): boolean {
    if (!config?.min && !config?.minTime) {
      return true;
    }
    const newTime = this.decrease(config, time, unit);

    return (!config?.min || config?.min?.isSameOrBefore?.(newTime))
      && (!config?.minTime || config?.minTime?.isSameOrBefore?.(this.utilsService?.onlyTime(newTime)));
  }

  shouldShowIncrease(config: ITimeSelectConfigInternal, time: Dayjs, unit: TimeUnit): boolean {
    if (!config?.max && !config?.maxTime) {
      return true;
    }
    const newTime = this.increase(config, time, unit);

    return (!config?.max || config?.max?.isSameOrAfter?.(newTime))
      && (!config?.maxTime || config?.maxTime?.isSameOrAfter?.(this.utilsService?.onlyTime(newTime)));
  }

  shouldShowToggleMeridiem(config: ITimeSelectConfigInternal, time: Dayjs): boolean {
    if (!config?.min && !config?.max && !config?.minTime && !config?.maxTime) {
      return true;
    }
    const newTime = this.toggleMeridiem(time);
    return (!config?.max || config?.max?.isSameOrAfter?.(newTime))
      && (!config?.min || config?.min?.isSameOrBefore?.(newTime))
      && (!config?.maxTime || config?.maxTime?.isSameOrAfter?.(this.utilsService?.onlyTime(newTime)))
      && (!config?.minTime || config?.minTime?.isSameOrBefore?.(this.utilsService?.onlyTime(newTime)));
  }
}

import {ECalendarValue} from '../../types/calendar-value-enum';
import {SingleCalendarValue} from '../../types/single-calendar-value';
import {ElementRef, Injectable} from '@angular/core';

import {Dayjs, UnitType} from 'dayjs';
import {CalendarValue} from '../../types/calendar-value';
import {IDate} from '../../models/date.model';
import {CalendarMode} from '../../types/calendar-mode';
import {DateValidator} from '../../types/validator.type';
import {ICalendarInternal} from '../../models/calendar.model';
import {dayjsRef} from '../../dayjs/dayjs.ref';

export interface DateLimits {
  minDate?: SingleCalendarValue;
  maxDate?: SingleCalendarValue;
  minTime?: SingleCalendarValue;
  maxTime?: SingleCalendarValue;
}

@Injectable({
  providedIn: 'root'
})
export class UtilsService {
  static debounce(func: Function, wait: number) {
    let timeout: ReturnType<typeof setTimeout>;
    return (...args: any[]) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, wait);
    };
  }

  createArray(size: number): number[] {
    return new Array(size).fill(1);
  }

  convertToDayjs(date: SingleCalendarValue, format: string): Dayjs {
    if (!date) {
      return dayjsRef();
    } else if (typeof date === 'string') {
      return dayjsRef(date, format);
    } else {
      return dayjsRef(date.toDate());
    }
  }

  isDateValid(date: string, format: string): boolean {
    if (date === '') {
      return true;
    }

    return dayjsRef(date, format, true).isValid();
  }

  // todo:: add unit test
  getDefaultDisplayDate(current: Dayjs,
                        selected: Dayjs[],
                        allowMultiSelect: boolean,
                        minDate: Dayjs): Dayjs {
    if (current) {
      return dayjsRef(current.toDate());
    } else if (minDate && minDate.isAfter(dayjsRef())) {
      return dayjsRef(minDate.toDate());
    } else if (allowMultiSelect) {
      if (selected && selected[selected.length]) {
        return dayjsRef(selected[selected.length].toDate());
      }
    } else if (selected && selected[0]) {
      return dayjsRef(selected[0].toDate());
    }

    return dayjsRef();
  }

  // todo:: add unit test
  getInputType(value: CalendarValue, allowMultiSelect: boolean): ECalendarValue {
    if (Array.isArray(value)) {
      if (!value.length) {
        return ECalendarValue.DayjsArr;
      } else if (typeof value[0] === 'string') {
        return ECalendarValue.StringArr;
      } else if (dayjsRef.isDayjs(value[0])) {
        return ECalendarValue.DayjsArr;
      }
    } else {
      if (typeof value === 'string') {
        return ECalendarValue.String;
      } else if (dayjsRef.isDayjs(value)) {
        return ECalendarValue.Dayjs;
      }
    }

    return allowMultiSelect ? ECalendarValue.DayjsArr : ECalendarValue.Dayjs;
  }

  // todo:: add unit test
  convertToDayjsArray(value: CalendarValue,
                      config: { allowMultiSelect?: boolean, format?: string }): Dayjs[] {
    let retVal: Dayjs[];
    switch (this.getInputType(value, config.allowMultiSelect ?? false)) {
      case (ECalendarValue.String):
        retVal = value ? [dayjsRef(<string>value, config.format, true)] : [];
        break;
      case (ECalendarValue.StringArr):
        retVal = (<string[]>value)
          .map(v => v ? dayjsRef(v, config.format, true) : null)
          .filter((d): d is Dayjs => d !== null && d !== undefined);
        break;
      case (ECalendarValue.Dayjs):
        retVal = value ? [dayjsRef((<Dayjs>value).toDate())] : [];
        break;
      case (ECalendarValue.DayjsArr):
        retVal = (<Dayjs[]>value || []).map(v => dayjsRef(v.toDate()));
        break;
      default:
        retVal = [];
    }

    return retVal;
  }

  // todo:: add unit test
  convertFromDayjsArray(format: string,
                        value: Dayjs[],
                        convertTo: ECalendarValue): CalendarValue {
    switch (convertTo) {
      case (ECalendarValue.String):
        return value[0] && value[0].format(format);
      case (ECalendarValue.StringArr):
        return value.filter(Boolean).map(v => v.format(format));
      case (ECalendarValue.Dayjs):
        return value[0] ? dayjsRef(value[0].toDate()) : value[0];
      case (ECalendarValue.DayjsArr):
        return value ? value.map(v => dayjsRef(v.toDate())) : value;
      default:
        return value;
    }
  }

  convertToString(value: CalendarValue, format: string): string {
    let tmpVal: string[];

    if (typeof value === 'string') {
      tmpVal = [value];
    } else if (Array.isArray(value)) {
      if (value.length) {
        tmpVal = (<SingleCalendarValue[]>value).map((v) => {
          return this.convertToDayjs(v, format).format(format);
        });
      } else {
        tmpVal = <string[]>value;
      }
    } else if (dayjsRef.isDayjs(value)) {
      tmpVal = [value.format(format)];
    } else {
      return '';
    }

    return tmpVal.filter(Boolean).join(' | ');
  }

  // todo:: add unit test
  clearUndefined<T extends { [key: string]: any }>(obj: T): T {
    if (!obj) {
      return obj;
    }

    Object.keys(obj).forEach((key) => (obj[key] === undefined) && delete obj[key]);
    return obj;
  }

  updateSelected(isMultiple: boolean,
                 currentlySelected: Dayjs[],
                 date: IDate,
                 granularity: UnitType = 'day'): Dayjs[] {
    if (isMultiple) {
      return !date.selected
        ? currentlySelected.concat([date.date])
        : currentlySelected.filter(d => !d.isSame(date.date, granularity));
    } else {
      return !date.selected ? [date.date] : [];
    }
  }

  closestParent(element: HTMLElement, selector: string): HTMLElement | null {
    if (!element) {
      return null;
    }
    const match = <HTMLElement>element.querySelector(selector);
    if (match) {
      return match;
    }
    if (element.parentElement) {
      return this.closestParent(element.parentElement, selector);
    }
    return null;
  }

  onlyTime(m: Dayjs): Dayjs {
    return m && dayjsRef.isDayjs(m) ? dayjsRef(m.format('HH:mm:ss'), 'HH:mm:ss') : dayjsRef('00:00:00', 'HH:mm:ss');
  }

  granularityFromType(calendarType: CalendarMode): UnitType {
    switch (calendarType) {
      case 'time':
        return 'second';
      case 'daytime':
        return 'second';
      default:
        return calendarType;
    }
  }

  createValidator({minDate, maxDate, minTime, maxTime}: DateLimits,
                  format: string,
                  calendarType: CalendarMode): DateValidator {
    let isValid: boolean;
    let value: Dayjs[];
    const validators: { key: string; isValid: () => boolean }[] = [];
    const granularity = this.granularityFromType(calendarType);

    if (minDate) {
      const md = this.convertToDayjs(minDate, format);
      validators.push({
        key: 'minDate',
        isValid: () => {
          const _isValid = value.every(val => val.isSameOrAfter(md, granularity));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    if (maxDate) {
      const md = this.convertToDayjs(maxDate, format);
      validators.push({
        key: 'maxDate',
        isValid: () => {
          const _isValid = value.every(val => val.isSameOrBefore(md, granularity));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    if (minTime) {
      const md = this.onlyTime(this.convertToDayjs(minTime, format));
      validators.push({
        key: 'minTime',
        isValid: () => {
          const _isValid = value.every(val => this.onlyTime(val).isSameOrAfter(md));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    if (maxTime) {
      const md = this.onlyTime(this.convertToDayjs(maxTime, format));
      validators.push({
        key: 'maxTime',
        isValid: () => {
          const _isValid = value.every(val => this.onlyTime(val).isSameOrBefore(md));
          isValid = isValid ? _isValid : false;
          return _isValid;
        }
      });
    }

    return (inputVal: CalendarValue) => {
      isValid = true;

      value = this.convertToDayjsArray(inputVal, {
        format,
        allowMultiSelect: true
      }).filter(Boolean);

      if (!value.every(val => val.isValid())) {
        return {
          format: {
            given: inputVal
          }
        };
      }

      interface Validator {
        key: string;
        isValid: () => boolean;
      }

      interface ErrorMap {
        [key: string]: {
          given: Dayjs[];
        };
      }

      interface Validator {
        key: string;
        isValid: () => boolean;
      }

      const errors: ErrorMap = validators.reduce<ErrorMap>((map: ErrorMap, err: Validator) => {
        if (!err.isValid()) {
          map[err.key] = {
        given: value
          };
        }

        return map;
      }, {});

      return !isValid ? errors : {};
    };
  }

  datesStringToStringArray(value: string): string[] {
    return (value || '').split('|').map(m => m.trim()).filter(Boolean);
  }

  getValidDayjsArray(value: string, format: string): Dayjs[] {
    return this.datesStringToStringArray(value)
      .filter(d => this.isDateValid(d, format))
      .map(d => dayjsRef(d, format));
  }

  shouldShowCurrent(showGoToCurrent: boolean,
                    mode: CalendarMode,
                    min: Dayjs,
                    max: Dayjs): boolean {
    return showGoToCurrent &&
      mode !== 'time' &&
      this.isDateInRange(dayjsRef(), min, max);
  }

  isDateInRange(date: Dayjs, from: Dayjs, to: Dayjs): boolean {
    if (!date) {
      return false;
    }

    if (!from && !to) {
      return true;
    }

    if (!from && to) {
      return date.isSameOrBefore(to);
    }

    if (from && !to) {
      return date.isSameOrAfter(from);
    }

    return date.isBetween(from, to, 'day', '[]');
  }

  convertPropsToDayjs(obj: { [key: string]: any }, format: string, props: string[]): void {
    props.forEach((prop) => {
      if (obj.hasOwnProperty(prop)) {
        obj[prop] = this.convertToDayjs(obj[prop], format);
      }
    });
  }

  shouldResetCurrentView<T extends ICalendarInternal>(prevConf: T, currentConf: T): boolean {
    if (prevConf && currentConf) {
      if (!prevConf.min && currentConf.min) {
        return true;
      } else if (prevConf.min && currentConf.min && !prevConf.min.isSame(currentConf.min, 'd')) {
        return true;
      } else if (!prevConf.max && currentConf.max) {
        return true;
      } else if (prevConf.max && currentConf.max && !prevConf.max.isSame(currentConf.max, 'd')) {
        return true;
      }

      return false;
    }

    return false;
  }

  getNativeElement(elem: HTMLElement | string | ElementRef): HTMLElement | null {
    if (!elem) {
      return null;
    } else if (typeof elem === 'string') {
      return document.querySelector(elem);
    } else if (elem instanceof ElementRef) {
      return elem.nativeElement;
    } else {
      return elem;
    }
  }
}

import {Injectable} from '@angular/core';
import dayjs, {Dayjs} from 'dayjs';
import {UtilsService} from '../common/services/utils/utils.service';
import {IYearCalendarConfig, IYearCalendarConfigInternal} from './year-calendar-config';
import { IGenCalendarParams } from './year-calendar.component';

export interface IDateCell {
  date: Dayjs;
  selected: boolean;
  current: boolean;
  disabled: boolean
  text: string;
}

@Injectable({
  providedIn: 'root'
})
export class YearCalendarService {
  readonly DEFAULT_CONFIG: IYearCalendarConfigInternal = {
    allowMultiSelect: false,
    yearFormat: 'YYYY',
    yearBtnFormat: 'YYYY',
    format: 'YYYY',
    numOfYearsPerPage: 24,
    numOfYearRows: 6,
    unSelectOnClick: true
  };

  constructor(private utilsService: UtilsService) {
  }

  getConfig(config: IYearCalendarConfig): IYearCalendarConfigInternal {
    const _config = <IYearCalendarConfigInternal>{
      ...this.DEFAULT_CONFIG,
      ...this.utilsService.clearUndefined(config)
    };

    this.validateConfig(_config);

  this.utilsService.convertPropsToDayjs(_config, _config.format ?? 'YYYY', ['min', 'max']);
  // If you use dayjs locale plugin, set locale here if needed

    return _config;
  }

    _generateCalendar<T>({
                        numOfRows,
                        numOfCells,
                        isDisabledCb,
                        getBtnTextCb,
                        selected,
                        config,
                        startDate,
                        granularity
                      }: IGenCalendarParams<T>): IDateCell[][] {
    const index = startDate.clone();

    return this.createArray(numOfRows).map(() => {
      return this.createArray(numOfCells / numOfRows).map(() => {
        const date = index.clone();
        const month = {
          date,
          selected: !!selected.find(s => index.isSame(s, granularity)),
          current: index.isSame(dayjs(), granularity),
          disabled: isDisabledCb(date, config),
          text: getBtnTextCb(config, date)
        };

        index.add(1, 'month');

        return month;
      });
    });
  }

  generateCalendar(config: IYearCalendarConfig, year: Dayjs, selected: Dayjs[] = []): IDateCell[][] {
    return this._generateCalendar<IYearCalendarConfig>({
      numOfRows: config.numOfYearRows ?? 0,
      numOfCells: config.numOfYearsPerPage ?? this.DEFAULT_CONFIG.numOfYearsPerPage,
      isDisabledCb: this.isYearDisabled,
      getBtnTextCb: this.getYearBtnText,
      selected,
      config,
      startDate: year.clone().startOf('year'),
      granularity: 'year'
    });
  }

  isYearDisabled(date: Dayjs, config: IYearCalendarConfig) {
    if (config.min && date.isBefore(config.min, 'year')) {
      return true;
    }
    return !!(config.max && date.isAfter(config.max, 'year'));
  }

  shouldShowLeft(min: Dayjs | undefined, currentMonthView: Dayjs): boolean {
    return min ? min.isBefore(currentMonthView, 'year') : true;
  }

  shouldShowRight(max: Dayjs | undefined, currentMonthView: Dayjs): boolean {
    return max ? max.isAfter(currentMonthView, 'year') : true;
  }

  getHeaderLabel(config: IYearCalendarConfig, year: Dayjs): string {
    if (config.yearFormatter) {
      return config.yearFormatter(year);
    }
    return year.format(config.yearFormat ?? 'YYYY');
  }

  getYearBtnText(config: IYearCalendarConfig, year: Dayjs): string {
    if (config.yearBtnFormatter) {
      return config.yearBtnFormatter(year);
    }
    return year.format(config.yearBtnFormat ?? 'YYYY');
  }

  getYearBtnCssClass(config: IYearCalendarConfig, year: Dayjs): string {
    if (config.yearBtnCssClassCallback) {
      return config.yearBtnCssClassCallback(year);
    }
    return '';
  }

  private validateConfig(config: IYearCalendarConfigInternal): void {
  }

  private createArray(length: number): number[] {
    return Array.from({ length }, (_, i) => i);
  }
}

